INSERT INTO `services`(id,services_name,description) VALUES(1,'Tatuajes personalizados','Los clientes tendrán la libertad de seleccionar motivos y diseños únicos, personalizando completamente su experiencia de tatuaje de acuerdo a sus preferencias y gustos.');
INSERT INTO `services`(id,services_name,description) VALUES(2,'Tatuajes del catálogo','Ofrecemos la realización de tatuajes basados en diseños predefinidos en nuestro catálogo. Los clientes pueden elegir entre una variedad de opciones estilizadas y probadas.');
INSERT INTO `services`(id,services_name,description) VALUES(3,'Restauración y rejuvenecimiento de trabajos','Nos especializamos en la restauración y rejuvenecimiento de tatuajes existentes.Nuestros expertos trabajan para mejorar y renovar tatuajes antiguos, devolviéndoles su vitalidad.');
INSERT INTO `services`(id,services_name,description) VALUES(4,'Colocación de piercings y dilatadores','Ofrecemos servicios profesionales para la colocación de piercings y dilatadores.Nuestro equipo garantiza procedimientos seguros y estilos variados para satisfacer las preferencias individuales de nuestros clientes.');
INSERT INTO `services`(id,services_name,description) VALUES(5,'Venta de piercings y otros artículos','Además de nuestros servicios de aplicación, ofrecemos una selección de piercings y otros artículos relacionados con el arte corporal. Los clientes pueden adquirir productos de calidad para complementar su estilo único.');
